package main

import (
	"fmt"
	"time"
)

var input = []byte("")

func main() {
	t0 := time.Now()
	a1 := idx(input, 4)
	d1 := time.Since(t0)
	fmt.Printf("Answer #1: %d in %v (%.3f MiB/s)\n", a1, d1, (float64(a1)/d1.Seconds())/(1024*1024))
	t0 = time.Now()
	a2 := idx(input, 14)
	d2 := time.Since(t0)
	fmt.Printf("Answer #2: %d in %v (%.3f MiB/s)\n", a2, d2, (float64(a2)/d2.Seconds())/(1024*1024))

}

func idx(d []byte, n int) int {
	ln, start, pos, state := len(d), 0, 0, 0

	for pos < ln {
		// Check on the run of unique chars
		if pos-start == n {
			// We found our index
			break
		}

		// Process next character
		c := 1 << int(d[pos]-'a') // cheap way of d[pos] % 32

		// remove state for all chars from start to and including a match
		for state&c > 0 {
			// remove side effect from d[start] from state
			state ^= 1 << int(d[start]-'a')
			// update start position
			start += 1
		}

		// update the state with current data
		state |= c
		// move pos
		pos += 1
	}
	return pos
}
